import React from 'react';
import PokeList from './components/PokeList';
import { pokemon } from './datasources/Array';
import './App.css';



class App extends React.Component {

  constructor(props) {
    super(props);

    pokemon.map(pokemonList => {
      return {...pokemonList, favorite: false};
    })
    this.state = ({
      pokemonList: pokemon
    })

    this.handleSelect = this.handleSelect.bind(this);
  }

  // Recojo los datos del pokemon seleciondado y le añado la  propiedad favorite con valor true
  // Comparo id del item selecionado con el de pokemon, y cuando coincida lo sustituyo por newItem que tiene la propiedad favorite
  handleSelect(item) {
    // const newItem = {...item, favorite: true};
    // console.log('_^_', newItem, newItem.id, item.id);

    this.setState(prevState => {
      const newItem = {...item, favorite: true};
      console.log('_^_', newItem, newItem.id, item.id);
      const newPokemonList = item.id===newItem.id ? {...prevState.pokemonList, newItem} : item;
      return{
        pokemonList: newPokemon
      }

    })

  }


  render() {
    return (
      <div className="app">
      <h1 className="app__title">Mi lista de pokemon</h1>
      <PokeList 
        pokemon={this.state.pokemonList}
        actionToSelect={this.handleSelect}
      />
      </div>
    );
  }

}

export default App;
