import React from 'react';
import PokeList from './components/PokeList';
import { pokemon } from './datasources/Array';
import './App.css';



class App extends React.Component {

  constructor(props) {
    super(props);

    const pokemonList = pokemon.map(poke => {
      return {...poke, favorite: false};
    })
    this.state = ({
      pokemonList: pokemonList
    })

    console.log('*', this.state.pokemonList);

    this.handleSelect = this.handleSelect.bind(this);
  }

  // Recojo los datos del pokemon seleciondado y le cambio la  propiedad favorite a true
  // Comparo id del item selecionado con el de pokemon, y cuando coincida lo sustituyo por newItem que tiene la propiedad favorite
  handleSelect(item) {

    const newItem = {...item, favorite: true};
    console.log('**', newItem, newItem.id, item.id);

    this.setState((prevState) => {

      const newFavorite = true;
      const id = '1';

      this.state.pokemonList.map(function(item) {
        if (item.id === id) {
          item.favorite = newFavorite;
          return item;
        }
      })
    })
  }


  render() {
    return (
      <div className="app">
      <h1 className="app__title">Mi lista de pokemon</h1>
      <PokeList 
        pokemon={this.state.pokemonList}
        actionToSelect={this.handleSelect}
      />
      </div>
    );
  }

}

export default App;
