import React from 'react';
import PokeList from './components/PokeList';
import {pokemon} from './components/Array';
import './App.css';

class App extends React.Component {

  constructor(props) {
    super(props);

    const pokeList = pokemon.map(poke => ({...poke, favorite: false}));

    this.state = {
      pokeList: pokeList
    }

    this.handleChoose = this.handleChoose.bind(this)
  }

  replacePokemon(arr, item, itemSelected){
    {arr.map(item => { itemSelected.id === item.id ? item.favorite = true : item.favorite = false })
      return(
        {item}
      );
    }
  }


  handleChoose (pokSelected) {
    const newPoke = {...pokSelected, favorite: true};

    this.setState((prevState) => {
      return({
        pokeList: newPoke
      });
    });
  }

  render () {

    return (
      <div className="app">
        <div className="block1">
          <h1 className="app__title">Mi lista de pokemon</h1>
          <PokeList 
            pokemon={this.state.pokeList} 
            actionToSelect={this.handleChoose}
          />
        </div>

        <div className="block2">
          <h2 className="favorites__title">Favoritos</h2>
          {/* <ul className="favorites__list">
            favorites.map((fav, indexFav) => {
              return(
                <li className="fav__item">
                  favorites.push.fav
                </li>
              );
            })
          </ul> */}

        </div>
      </div>
    );
  }

}

export default App;
