import React from 'react';
import PokeList from './components/PokeList';
import { pokemon } from './datasources/Array';
import './App.css';



class App extends React.Component {

  constructor(props) {
    super(props);

    this.state = ({
      pokemon: pokemon
    })

  }

  // Recojo los datos del pokemon seleciondado y le añado la  propiedad favorite con valor true
  // Comparo id del item selecionado con el de pokemon, y cuando coincida lo sustituyo por newItem que tiene la propiedad favorite
  handleSelect(item) {
    const newItem = {...item, favorite: true};
    console.log('_^_', newItem);
    if(this.state.pokemon.id===newItem.id) {
      this.state.push(newItem);
    }
  }


  render() {
    return (
      <div className="app">
      <h1 className="app__title">Mi lista de pokemon</h1>
      <PokeList 
        pokemon={this.state.pokemon}
        actionToSelect={this.handleSelect}
      />
      </div>
    );
  }

}

export default App;
